import React, { useState, useEffect } from 'react';

const initialSudoku = [
  [5, 3, 4, 6, 7, 8, 9, 1, 2],
  [6, 7, 2, 1, 9, 5, 3, 4, 8],
  [1, 9, 8, 3, 4, 2, 5, 6, 7],
  [8, 5, 9, 7, 6, 1, 4, 2, 3],
  [4, 2, 6, 8, 5, 3, 7, 9, 1],
  [7, 1, 3, 9, 2, 4, 8, 5, 6],
  [9, 6, 1, 5, 3, 7, 2, 8, 4],
  [2, 8, 7, 4, 1, 9, 6, 3, 5],
  [3, 4, 5, 2, 8, 6, 1, 7, 0]  // Only one number missing
];

function Sudoku({ onComplete }) {
  const [puzzle, setPuzzle] = useState(initialSudoku);
  const [timeLeft, setTimeLeft] = useState(5 * 60); // 5 minutes in seconds

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => {
        if (prevTime <= 1) {
          clearInterval(timer);
          onComplete(false);
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [onComplete]);

  const handleInputChange = (row, col, value) => {
    const newPuzzle = puzzle.map((r) => [...r]);
    newPuzzle[row][col] = value === '' ? 0 : parseInt(value, 10);
    setPuzzle(newPuzzle);

    if (isSudokuComplete(newPuzzle)) {
      onComplete(true);
    }
  };

  const isSudokuComplete = (puzzle) => {
    for (let i = 0; i < 9; i++) {
      for (let j = 0; j < 9; j++) {
        if (puzzle[i][j] === 0) return false;
      }
    }
    return true;
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
  };

  return (
    <div className="Sudoku">
      <h2>Final Challenge: Complete the Sudoku</h2>
      <p>Fill in the last number to unlock your gift!</p>
      <p>Time remaining: {formatTime(timeLeft)}</p>
      <div className="sudoku-grid">
        {puzzle.map((row, rowIndex) => (
          <div key={rowIndex} className="sudoku-row">
            {row.map((cell, colIndex) => (
              <input
                key={`${rowIndex}-${colIndex}`}
                type="number"
                min="1"
                max="9"
                value={cell === 0 ? '' : cell}
                onChange={(e) => handleInputChange(rowIndex, colIndex, e.target.value)}
                className={initialSudoku[rowIndex][colIndex] !== 0 ? 'initial' : ''}
                readOnly={initialSudoku[rowIndex][colIndex] !== 0}
              />
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Sudoku;
