import React, { useState } from 'react';
import Question from './Question';
import './App.css';

const questions = [
  { question: "What's Serena's favorite color?", answer: "PINK" },
  { question: "What's Serena's zodiac sign?", answer: "LEO" }
];

function App() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);

  const handleNextQuestion = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }
    setCurrentQuestionIndex(currentQuestionIndex + 1);
  };

  const openYouTubeLink = () => {
    window.open('https://www.youtube.com/watch?v=k12NZLh_Xvg', '_blank');
  };

  return (
    <div className="App">
      <h1>üéâ Serena's Birthday Questionnaire üéÇ</h1>
      {currentQuestionIndex < questions.length ? (
        <Question
          question={questions[currentQuestionIndex].question}
          answer={questions[currentQuestionIndex].answer}
          onNextQuestion={handleNextQuestion}
        />
      ) : (
        <div className="game-over">
          <h2>üéà Happy Birthday, Serena! üéà</h2>
          <p>Your score: {score} out of {questions.length}</p>
          <button onClick={openYouTubeLink}>Click for your surprise! üéÅ</button>
        </div>
      )}
    </div>
  );
}

export default App;
