import React, { useState, useEffect } from 'react';

function Question({ question, answer, onNextQuestion }) {
  const [guess, setGuess] = useState(Array(answer.length).fill(''));
  const [isCorrect, setIsCorrect] = useState(null);

  useEffect(() => {
    setGuess(Array(answer.length).fill(''));
    setIsCorrect(null);
  }, [answer]);

  const handleInputChange = (index, value) => {
    const newGuess = [...guess];
    newGuess[index] = value.toUpperCase();
    setGuess(newGuess);

    if (newGuess.join('') === answer) {
      setIsCorrect(true);
      setTimeout(() => onNextQuestion(true), 1000);
    } else if (newGuess.filter(Boolean).length === answer.length) {
      setIsCorrect(false);
    }
  };

  return (
    <div className="Question">
      <h3>{question}</h3>
      <div className="answer-boxes">
        {answer.split('').map((letter, index) => (
          <input
            key={index}
            type="text"
            maxLength="1"
            value={guess[index]}
            onChange={(e) => handleInputChange(index, e.target.value)}
            className={
              isCorrect === null
                ? ''
                : guess[index] === answer[index]
                ? 'correct'
                : 'incorrect'
            }
          />
        ))}
      </div>
      {isCorrect !== null && (
        <p>{isCorrect ? 'ðŸ”“ Correct! Unlocking next level...' : 'ðŸ”’ Access Denied. Try again!'}</p>
      )}
    </div>
  );
}

export default Question;
