import React, { useState, useEffect } from 'react';

const initialSudoku = [
  [5, 3, 0, 0, 7, 0, 0, 0, 0],
  [6, 0, 0, 1, 9, 5, 0, 0, 0],
  [0, 9, 8, 0, 0, 0, 0, 6, 0],
  [8, 0, 0, 0, 6, 0, 0, 0, 3],
  [4, 0, 0, 8, 0, 3, 0, 0, 1],
  [7, 0, 0, 0, 2, 0, 0, 0, 6],
  [0, 6, 0, 0, 0, 0, 2, 8, 0],
  [0, 0, 0, 4, 1, 9, 0, 0, 5],
  [0, 0, 0, 0, 8, 0, 0, 7, 9]
];

function Sudoku({ onComplete }) {
  const [puzzle, setPuzzle] = useState(initialSudoku);
  const [timeLeft, setTimeLeft] = useState(10 * 60); // 10 minutes in seconds
  const [selectedCell, setSelectedCell] = useState(null);

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => {
        if (prevTime <= 1) {
          clearInterval(timer);
          onComplete(false);
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [onComplete]);

  const handleInputChange = (row, col, value) => {
    const newPuzzle = puzzle.map((r) => [...r]);
    newPuzzle[row][col] = value === '' ? 0 : parseInt(value, 10);
    setPuzzle(newPuzzle);

    if (isSudokuComplete(newPuzzle)) {
      onComplete(true);
    }
  };

  const isSudokuComplete = (puzzle) => {
    for (let i = 0; i < 9; i++) {
      for (let j = 0; j < 9; j++) {
        if (puzzle[i][j] === 0) return false;
      }
    }
    return true; // For simplicity, we're not checking if the solution is correct
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
  };

  const handleCellFocus = (row, col) => {
    setSelectedCell(`${row}-${col}`);
  };

  return (
    <div className="Sudoku">
      <h2>Final Challenge: Sudoku</h2>
      <p>Complete the puzzle to unlock your gift!</p>
      <p>Time remaining: {formatTime(timeLeft)}</p>
      <div className="sudoku-grid">
        {puzzle.map((row, rowIndex) => (
          <div key={rowIndex} className="sudoku-row">
            {row.map((cell, colIndex) => (
              <input
                key={`${rowIndex}-${colIndex}`}
                type="text"
                inputMode="numeric"
                pattern="[1-9]*"
                maxLength="1"
                value={cell === 0 ? '' : cell}
                onChange={(e) => handleInputChange(rowIndex, colIndex, e.target.value)}
                onFocus={() => handleCellFocus(rowIndex, colIndex)}
                className={`
                  ${initialSudoku[rowIndex][colIndex] !== 0 ? 'initial' : ''}
                  ${selectedCell === `${rowIndex}-${colIndex}` ? 'selected' : ''}
                `}
                readOnly={initialSudoku[rowIndex][colIndex] !== 0}
              />
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Sudoku;
