import React, { useState } from 'react';
import Question from './Question';
import './App.css';

const questions = [
  { question: "What's Serena's favorite color?", answer: "PINK" },
  { question: "What's Serena's birth month?", answer: "AUGUST" },
  { question: "What's Serena's favorite animal?", answer: "CAT" },
  { question: "What's Serena's favorite flower?", answer: "ROSE" },
  { question: "What's Serena's zodiac sign?", answer: "LEO" }
];

function App() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [isVerified, setIsVerified] = useState(false);

  const handleNextQuestion = (isCorrect) => {
    if (isCorrect) {
      if (currentQuestionIndex < questions.length - 1) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
      } else {
        setIsVerified(true);
      }
    }
  };

  const openYouTubeLink = () => {
    window.open('https://www.youtube.com/watch?v=k12NZLh_Xvg', '_blank');
  };

  return (
    <div className="App">
      <h1>üîê Serena's Birthday Vault üéÅ</h1>
      {!isVerified ? (
        <>
          <h2>Identity Verification Required</h2>
          <p>Answer these questions to unlock your birthday surprise!</p>
          <Question
            question={questions[currentQuestionIndex].question}
            answer={questions[currentQuestionIndex].answer}
            onNextQuestion={handleNextQuestion}
          />
        </>
      ) : (
        <div className="vault-unlocked">
          <h2>üéâ Identity Confirmed! üéâ</h2>
          <p>Congratulations, Serena! You've unlocked your birthday surprise!</p>
          <button onClick={openYouTubeLink}>Reveal Your Gift! üéÅ</button>
        </div>
      )}
    </div>
  );
}

export default App;
