import React, { useState, useEffect, useRef } from 'react';

function Question({ question, answer, onNextQuestion }) {
  const [guess, setGuess] = useState(Array(answer.length).fill(''));
  const [isCorrect, setIsCorrect] = useState(null);
  const inputRefs = useRef([]);

  useEffect(() => {
    setGuess(Array(answer.length).fill(''));
    setIsCorrect(null);
    if (inputRefs.current[0]) {
      inputRefs.current[0].focus();
    }
  }, [answer]);

  const handleKeyDown = (e, index) => {
    if (e.key === 'Backspace' && index > 0 && !guess[index]) {
      e.preventDefault();
      const newGuess = [...guess];
      newGuess[index - 1] = '';
      setGuess(newGuess);
      inputRefs.current[index - 1].focus();
    }
  };

  const handleInputChange = (index, value) => {
    if (index >= answer.length) return;

    const newGuess = [...guess];
    newGuess[index] = value.toUpperCase();
    setGuess(newGuess);

    if (value && index < answer.length - 1) {
      inputRefs.current[index + 1].focus();
    }

    if (newGuess.join('') === answer) {
      setIsCorrect(true);
      setTimeout(() => onNextQuestion(true), 500);
    } else if (newGuess.filter(Boolean).length === answer.length) {
      setIsCorrect(false);
    }
  };

  return (
    <div className="Question">
      <h3>{question}</h3>
      <div className="answer-boxes">
        {answer.split('').map((letter, index) => (
          <input
            key={index}
            ref={(el) => (inputRefs.current[index] = el)}
            type="text"
            maxLength="1"
            value={guess[index]}
            onChange={(e) => handleInputChange(index, e.target.value)}
            onKeyDown={(e) => handleKeyDown(e, index)}
            className={
              isCorrect === null
                ? ''
                : guess[index] === answer[index]
                ? 'correct'
                : 'incorrect'
            }
          />
        ))}
      </div>
      {isCorrect === false && <p>ðŸ”’ Access Denied. Try again!</p>}
    </div>
  );
}

export default Question;
